import React from 'react'

export const  Body = (props)=>{
    let bodyStyle = {
        textAlign: 'center',
    margin: '5vh 5% 0px',
    }
    let bodyDiv = {
        margin: '3% 20vw',
    }
    let pStyle = {
        textAlign: 'left',
    marginTop: '2vh'
    }
    return (
        <div style={bodyStyle}>
            <div style={bodyDiv}>
                <h1>About Us</h1>
                <div>
                    <p style={pStyle}>ETark is an automated complaint resolution platform for smartphone complaints. We help in resolving both technical and non-technical smartphone problems via. our service offerings. ETark was conceptualized after identifying the plight of hapless smartphone customers and the inefficiency of the service centers.
                    <br /><br />
                    How ETark helps customers?
                    </p>
                </div>
            </div>
        </div>
    )
}
