import React from 'react';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';


export const NewAccordion2 = () => {
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };


    let paraStyle = {
        textAlign: 'left',
        marginTop: '2vh',
    }
    let li = {
        marginBottom: '1.5vh',
        marginTop: '1.5vh',
    }
    let ul = {
        marginLeft: '10vh',
    }

    return (
        <div >
            <Accordion expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel1bh-content"
                    id="panel1bh-header"
                >
                    <Typography >Chances of winning</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <div>
                        <Typography>

                            <p style={paraStyle}>
                                Chances of winning tells you how likely you are to win in a consumer court had the complaint been filed there
                                <br />
                                Or Alternatively,
                                <br />
                                How likely you are to win in a negotiation with the other party (device manufacturer or seller) by quoting your winning chances in a consumer court as a reference

                            </p>

                        </Typography>

                    </div>
                </AccordionDetails>
            </Accordion>

            <Accordion expanded={expanded === 'panel4'} onChange={handleChange('panel4')}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel4bh-content"
                    id="panel4bh-header"
                >
                    <Typography >Expected compensation</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <div margin="3%"></div>
                    <Typography>
                        <p style={paraStyle}>
                            <b>Although device Insurance companies promise to insure your device from future damage or malfunctioning:
                            </b>
                        </p>
                        <p style={paraStyle}>
                            They often result in long waiting time to get clearance from the smartphone manufacturer to get the ensuing servicing covered which offsets the cost savings that they promise through their insurance
                            <br /><br />
                            All these approaches have their own share of flaws.
                            <br /><br />
                            ETark firmly believes that the best way to solve any technical issue is through authorized service centers only provided the inefficiencies of the service centers are removed and the customer doesnâ€™t have to go through any hassle. This is exactly where we step in and we ensure that both the customer and the service center are seamlessly connected!
                        </p>

                    </Typography>
                </AccordionDetails>
            </Accordion>
        </div>
    );
}
